import './Discover.css';
import { useState, useEffect } from 'react';
import { Route, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import axios from 'axios';

import NavBar from '../NavBar/NavBar';
import ExperiencePage from '../ExperiencePage/ExperiencePage';

function Discover() {

    const [experiences, setExperiences] = useState([]);
    const fetchExperiences = async () => {
        try {
            const response = await axios.get('/api/experiences/fetch_experiences/');
            setExperiences(response.data);
        } catch(error) {
            console.error('Error fetching experiences:', error);
        }
    }

    const [selectedExperience, setSelectedExperience] = useState(null);
    const handleSelectExperience = async (experience_id) => {
        try {
            const response = await axios.get("api/experiences/fetch_experience/", {params: { experience_id }});
            const experience = response.data;
            setSelectedExperience(experience);
            handleLoadExperiencePage(experience);
            console.log("Selected Experience:", experience);
        } catch(error) {
            console.error("Error selecting experience:", error);
        }
    };

    const handleLoadExperiencePage = (selectedExperience) => {
        if (selectedExperience) {
            const navigate = useNavigate();
            navigate(`/discover/${selectedExperience.rootPlaceId}`, {
                state: {
                    icon: selectedExperience.icon,
                    name: selectedExperience.name,
                    url: selectedExperience.url,
                    creator: selectedExperience.creator,
                    description: selectedExperience.description,
                    genre: selectedExperience.genre,
                    genre_l1: selectedExperience.genre_l1,
                    genre_l2: selectedExperience.genre_l2,
                    maxPlayers: selectedExperience.maxPlayers,
                    created: selectedExperience.created
                }
            });
        }
    }

    useEffect(() => {
        fetchExperiences();
    }, [])

    return (
        <div className='Discover'>
            <NavBar />
            <motion.div
                initial={{ opacity: 0, scale: 0.7 }}
                animate={{ opacity: 1, scale: 1 }}
                exit={{ opacity: 1, scale: 0 }}
                transition={{ duration: 0.8 }}
            >
                <h1> Discover Experiences </h1>
            </motion.div>
            <motion.div
                initial={{ opacity: 0, scale: 0.7 }}
                animate={{ opacity: 1, scale: 1 }}
                exit={{ opacity: 1, scale: 0 }}
                transition={{ duration: 0.8 }}
                className='discover-content'
            >
                <div className='experience-category'>
                    <h1> Test Experiences </h1>
                    <div className='category-list'>
                        <button className='previous-button'> Prev </button>
                        {experiences.length > 0 ? (
                            <div className='experience-list'>
                                {experiences.map((experience, index) => (
                                    <div key={index} onClick={experience.id} className='experience-item'>
                                        <img src={experience.icon} className='experience-icon' />
                                        <h2 className='experience-name'>{experience.name}</h2>
                                    </div>
                                ))}
                                {/* <div className='experience-item'>
                                    <img src={'https://tr.rbxcdn.com/180DAY-57bcbdcf22425abcb60b41c7574fa80b/512/512/Image/Png/noFilter'} className='experience-icon' />
                                    <h2 className='experience-name'>ðŸ”¹ Jailbreak</h2>
                                </div> */}
                            </div>
                        ) : (
                            <div className='empty-list'>
                                <h2 > No experiences found </h2>
                            </div>
                        )}
                        <button className='next-button' > Next </button>
                    </div>
                </div>
            </motion.div>
        </div>
    );
}

export default Discover;
