import "./ExperiencePage.css";
import React, { use } from "react";
import { motion } from "framer-motion";
import { useState } from "react";
import axios from "axios";

function ExperiencePage(experience) {

    const [experienceData, setExperienceData] = useState({
        rootPlaceId: experience.rootPlaceId,
        name: experience.name,
        url: experience.url,
        description: experience.description,
        creator: experience.creator,
        genre: experience.genre,
        genre_l1: experience.genre_l1,
        genre_l2: experience.genre_l2,
        maxPlayers: experience.maxPlayers,
        created: experience.created,
        icon: experience.icon,
    });

    const handleExperienceUrlDirection = (experience_url) => {
        if (experience_url) {
            window.open(experience_url, "_blank");
        } else {
            console.error("Experience URL is empty");
        }
    };

    return (
        <div className="ExperiencePage">
            <motion.div
                initial={{ opacity: 0, scale: 0.7 }}
                animate={{ opacity: 1, scale: 1 }}
                exit={{ opacity: 1, scale: 0 }}
                transition={{ duration: 0.8 }}
            >
                <h1> Experience Page </h1>
            </motion.div>
            <motion.div
                initial={{ opacity: 0, scale: 0.7 }}
                animate={{ opacity: 1, scale: 1 }}
                exit={{ opacity: 1, scale: 0 }}
                transition={{ duration: 0.8 }}
                className="experience-content"
            >
                <div className="experience-details">
                    <img src={experienceData.icon} className="experience-icon" />
                    <h2 className="experience-name">{experienceData.name}</h2>
                    <p className="experience-creator">Creator: {experienceData.creator}</p>
                    <button className="url-button" onClick={handleExperienceUrlDirection.bind(this, experienceData.url)}>
                        Visit on Roblox
                    </button>
                    <p className="experience-description">{experienceData.description}</p>
                    <div className="experience-minor-details">
                        <p className="experience-genre">Genre: {experienceData.genre}</p>
                        <p className="experience-genre-l1">Genre 2: {experienceData.genre_l1}</p>
                        <p className="experience-genre-l2">Genre 3: {experienceData.genre_l2}</p>
                        <p className="experience-maxPlayers">Max Players: {experienceData.maxPlayers}</p>
                        <p className="experience-created">Created: {new Date(experienceData.created).toLocaleDateString()}</p>
                    </div>
                </div>
            </motion.div>
        </div>
    );
}

export default ExperiencePage;
