import "./ExperiencePage.css";
import NavBar from "../NavBar/NavBar";
import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import axios from "axios";
import { Route, useParams, useNavigate, window } from "react-router-dom";

function ExperiencePage(experience) {

    const handleExperienceUrlDirection = (experience_url) => {
        if (experience_url) {
            window.open(experience_url, "_blank");
        } else {
            console.error("Experience URL is empty");
        }
    };
    return (
        <div className="ExperiencePage">
            <NavBar />
            <motion.div
                initial={{ opacity: 0, scale: 0.7 }}
                animate={{ opacity: 1, scale: 1 }}
                exit={{ opacity: 1, scale: 0 }}
                transition={{ duration: 0.8 }}
            >
                <h1> Experience Page </h1>
            </motion.div>
            <motion.div
                initial={{ opacity: 0, scale: 0.7 }}
                animate={{ opacity: 1, scale: 1 }}
                exit={{ opacity: 1, scale: 0 }}
                transition={{ duration: 0.8 }}
                className="experience-content"
            >
                <div className="experience-details">
                    <img src={experience.icon} className="experience-icon" />
                    <h2 className="experience-name">{experience.name}</h2>
                    <p className="experience-creator">Creator: {experience.creator}</p>
                    <button className="url-button" onClick={handleExperienceUrlDirection.bind(this, experience.url)}>
                        Visit on Roblox
                    </button>
                    <p className="experience-description">{experience.description}</p>
                    <div className="experience-minor-details">
                        <p className="experience-genre">Genre: {experience.genre}</p>
                        <p className="experience-genre-l1">Genre 2: {experience.genre_l1}</p>
                        <p className="experience-genre-l2">Genre 3: {experience.genre_l2}</p>
                        <p className="experience-maxPlayers">Max Players: {experience.maxPlayers}</p>
                        <p className="experience-created">Created: {new Date(experience.created).toLocaleDateString()}</p>
                    </div>
                </div>
            </motion.div>
        </div>
    );
}

export default ExperiencePage;
